Community goal.script:
{_ Set Context _}
{SetState('eddi_context_last_subject', 'community goal')}
{_ Save goal location to context if there is only one goal present _}
{if len(event.cgid) = 1:
   {SetState('eddi_context_goal_system_name', event.system[0])}
   {SetState('eddi_context_goal_station_name', event.station[0])}
}

{F('Honorific')}, 

{if len(event.name) > 1:
    {OneOf("hai dimostrato interesse per i", "sto tenendo traccia per te dei")}
    seguenti eventi comunitarii:
|else: 
    L'evento comunitario,    
}

{set cur to 0}
{while cur < len(event.name):

   {if cur = 0 && len(event.name) = 1:
      {_ nothing _}
   |elif cur = 0 && len(event.name) > 1:
      Il primo,
   |elif cur < (len(event.name) - 1):
      Il successivo, 
   |else:
      L'ultimo,
   }

   {event.name[cur]},

   {if event.iscomplete[cur]:
      {OneOf("è stato completato", "è terminato")} 
   |else: 
      {if (event.expiry[cur] / 3600) < 12:
         {OneOf("è prossimo ad essere terminato", 
                "dovrebbe terminare entro {Humanise(round(event.expiry[cur] / 3600, 1))} ore", 
                "è quasi terminato")}
      |else:
         {OneOf("è ancora in corso", 
                "non è ancora terminato", 
                "è ancora in corso")} 
      }
   }

   {if event.tier[cur]:
       {OneOf("ed ha raggiunto il", "ed è al momento al")} {event.tier[cur]}.
       {if event.contribution[cur] > 0:
           La tua ricomensa è 
           {Occasionally(2, "allo stato attuale di")}
           {Occasionally(2, "quantificabile in")}
           {Humanise(event.tierreward[cur])} crediti
           {Occasionally(2, "nel {event.percentileband[cur]}esimo percentile")}
       }
   }.

   {_ Create a comparison function _}
   {set compare(stat) to:     
       {if stat < 0.01:
            solo una {OneOf("frazione", "piccola porzione")} della            
       |elif stat < 0.10:
            meno di un decimo della
       |elif stat < 0.25:
            meno di un quarto della
       |elif stat < 0.5:
            meno della metà della
       |elif stat < 0.75:
            meno della
       |elif stat < 0.96: 
            {OneOf("appena","di poco")}
            sotto la
       |elif stat < 1.05:
            {OneOf("approssimativamente","sostanzialmente","praticamente")} 
            nella 
       |elif stat < 1.25:
            leggermente sopra la
       |elif stat < 1.5:
            sopra la
       |elif stat < 2:
            ben al di sopra della
       |else:
            ampiamente sopra la
       }
    }
    {set compare_contribution to (event.contribution[cur] / round(event.total[cur] / event.contributors[cur], 0))}

    {if compare_contribution != 0:
       {if event.toprank[cur]: 
          Congratulazioni! 
          Sei uno dei {event.topranksize[cur]} primi comandanti dell'evento, con un contributo 
          {compare(compare_contribution)}
          media.
       |else:
          {Occasionally(2, "Hai contribuito 
          {compare(compare_contribution)}
          media.")}
       }
    }

    {if !event.iscomplete[cur]:
       Potresti 
       {if event.contribution[cur] > 0: 
          {OneOf("contribuire di più", "offrire un ulteriore contributo alla causa")} 
       |else: 
          {OneOf("contribuire alla causa", "partecipare all'evento")}
       }
       {if event.station[cur] = station.name:
          {Occasionally(2, "in questa stessa stazione")}
          {Occasionally(2, "nella presente stazione")}.
       |elif event.system[cur] = system.name:
          at {P(event.station[cur])}
          {Occasionally(2, "in questo sistema")}
          {Occasionally(2, "nel sistema in cui siamo ora", "nell'attuale sistema")}.
       |else:
          a {P(event.station[cur])},
          in {OneOf("{P(event.system[cur])}", "il sistema {P(event.system[cur])}" )}.
       }
    }

    {set cur to cur + 1}
    {Pause(1000)}
}

