Entered signal source.script:
{set warninglevel to:
   {set entered to OneOf("Decellerazione", "Velocità subluce raggiunta", "Passaggio a velocità subluce")}
   {if event.threat < 2:
      {entered}
   |elif event.threat = 2:
      Cautela: {entered} presso zona di medio rischio
   |elif event.threat = 3:
      Attenzione: {entered} presso zona ad alto rischio
   |elif event.threat = 4:
      Pericolo: {entered} presso zona ad altissimo rischio
   |elif event.threat = 5:
      Allarme generale: segnalato altissimo pericolo
   |elif event.threat = 6:
      Allarme generale: segnalato pericolo estremo
   |elif event.threat = 7:
      Allarme generale: segnalato pericolo massimo
   |else:
      Allarme generale: segnalata pericolosità di livello {event.threat}. Il massimo registrabile.
   }
}

{set source to token(event.source, "$USS_Type_", 1)}

{if source = "Aftermath;":
   {set source to OneOf(". segni di conflitto recenti", ". sembra esserci stato uno scontro di recente")}
|elif ((source = "Anomaly;" || source = "Salvage;") && event.threat = 2):
   {set warninglevel to entered}
   {set source to OneOf(". rilevati segnali alieni", ". rilevati segnali di natura xeno", ". rilvati segnali di natura non umana", ". rilevati segnali di natura Targoid")}
|elif (source = "Anomaly;" && event.threat = 4):
   {set warninglevel to entered}
   {set source to OneOf(". rilevati segnali alieni", ". rilevati segnali di natura xeno", ". rilvati segnali di natura non umana", ". rilevati segnali di natura Targoid")}
|elif source = "Anomaly;":
   {set source to OneOf(".rilevati segnali anomali", ". rilevati segnali sconosciuti", ". rilevati segnali non identificabili")}
|elif source = "Ceremonial;":
    {set source to ". riceviamo comunicazioni cerimoniali"}
|elif source = "Convoy;":
    {set source to ". riceviamo segnali di un convoio"}
|elif source = "DisruptedWakeEchoes;":
    {set source to ""}
|elif source = "DistressSignal;":
   {set source to ". rileviamo richiesta di soccorso"}
|elif source = "MissionTarget;":
   {set source to OneOf(". rileviamo obiettivo della missione", ". rileviamo obiettivo della missione")}
|elif source = "NonHuman;":
   {set source to OneOf(". rilevati segnali alieni", ". rilevati segnali di natura xeno", ". rilvati segnali di natura non umana", ". rilevati segnali di natura Targoid")}
|elif source = "Salvage;":
    {set source to ". rilevati segnali flebili"}
|elif source = "ValuableSalvage;":
    {set source to ". rilevati segnali codificati"}
|elif source = "VeryValuableSalvage;":
    {set source to ". rilevati segnali ad ampio raggio"}
|elif source = "WeaponsFire;":
   {set source to OneOf(". rilevata attività di combattimento", ". rilevati colpi di arma")}
}

{Pause(2000)}
{warninglevel} {source}. 

{if system.state = "Anarchy" && source = "degraded emissions" && system.population > 0: 
   {Occasionally(2, "
      cautela:
      {set local to OneOf('locali', 'locali')}
      {set chatter to OneOf('comunicazioni', 'trasmissioni', 'segnali')}
      {set indicate to OneOf('suggeriscono', 'riportano')}
      {OneOf('trasmissioni {local} {indicate}', 
         '{chatter} {local} {indicate}')}
      {Occasionally(3, 'che ci sarebbero')}
      {OneOf('elevate', 'numerose', 'frequenti')}
      {OneOf('attività', 'aggressioni')}
      {OneOf('criminali', 
             'da parte di pirati')}
      nel {OneOf('sistema', 'sistema')}.
      {OneOf('raccomando', 'suggerisco')} {OneOf('massima accortezza', 'cautela', 'attenzione')} 
      {Occasionally(3,' {F(\\'Honorific\\')}' )}.
   ")}
}

