FSD engaged.script:
{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'engage')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_system_system', event.system)}
{SetState('eddi_context_fsd_target', event.target)}

{if event.target = 'Hyperspace':
    {_ Rewrite context to represent a jump }
    {SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}

    {set system to SystemDetails(event.system)}

    {_ Wait for the FSD countdown and initial jump}
    {Pause(1000)}

    {OneOf("Destinazione agganciata","transizione nell'iperspazio in corso","telemetria ottenuta")}.
    {OneOf("Siamo diretti al", "stiamo per giungere nel", "salto verso il")} sistema di {P(system.name)}.
    {if system.name = homesystem.name:
        Bentornato a casa, {F("Honorific")}
    |else:
        {set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}
        {if slice(event.stellarclass, 0, 1) = "D":
            Cautela: nana bianca rilevata.
        |elif event.stellarclass = "N":
            Cautela: stella a neutroni rilevata.
        |elif event.stellarclass = "H":
            Cautela: buco nero rilevato.
        |elif find(scoopables, event.stellarclass) = -1:
            {Occasionally(2, "nota bene: ")}
            la stella di destinazione non è in grado di ricaricare le celle carburante.
        }
        
        {if lastsystem.allegiance && lastsystem.allegiance != "None" && lastsystem.allegiance != "Independent" && (!system.allegiance || system.allegiance = "None" || system.allegiance = "Independent"):
            {OneOf("stiamo lasciando il", "abbiamo abbandonato il", "non siamo più nel")} territorio  {lastsystem.allegiance}.
        |elif lastsystem.allegiance && lastsystem.allegiance != system.allegiance && system.allegiance && system.allegiance != "None":
            {OneOf("siamo in", "risulta essere", "il sistema è")} territorio {system.allegiance}.
            {if cmdr.title != "comandante":
                Benvenuto {F("Honorific")}.
            }
        }

        {if system.visits = 0:
            è la prima volta che visiti questo sistema.
        |elif system.visits = 1:
            Questa è la seconda volta che visiti il sistema.
        |elif system.visits = 2:
            {Occasionally(2, "Questa è la terza volta che visiti il sistema.")}
        |elif system.visits = 3:
            {Occasionally(2, "Questa è la quarta volta che visiti il sistema.")}
        |else:
            {Occasionally(3, "Hai visitato il sistema {system.visits} volte.")}
        }

        {if !system.population && lastsystem.population:
            {OneOf("Questo sistema non è abitato","Nel sistema non c'è presenza umana", "il sistema non è stato colonizzato")}.
        |elif system.population:
                {F("System report")}
        }

        {if system.distancefromhome:
            {Occasionally(7, "{OneOf(\"Sei\", \"L'attuale sistema è\", \"Siamo ora\")} a {Humanise(system.distancefromhome)} anni luca da {OneOf(\"casa\", \"{P(homesystem.name)}\")}.")}
        }

        {if system.comment:
            Hai preso un appunto su questo sistema.  Eccolo: {system.comment}.
        }
    }
}

Fuel check.script:
{_ Fuel Check _}
{_ Report fuel level _}

{if ship.name = state.eddi_fuelcheck_shipname:
    {if state.eddi_context_fuel_used > state.eddi_fuelcheck_maxfuel:
        {SetState('eddi_fuelcheck_maxfuel', state.eddi_context_fuel_used)}
    }
|else:
    {SetState('eddi_fuelcheck_maxfuel', state.eddi_context_fuel_used)}
    {SetState('eddi_fuelcheck_shipname', ship.name)}
}

{set maxfuel to state.eddi_fuelcheck_maxfuel}
{set fuelremaining to state.eddi_context_fuel_remaining}
{set maxjump to fuelremaining/maxfuel}
{set currentfuel to round(fuelremaining / ship.fueltanktotalcapacity * 100, 0)}
{set shipsfuel to:
   {Occasionally(2, "" )} 
   {OneOf("il livello del carburante è", "il serbatoio di carburante è")} {Occasionally(2, "")} {Occasionally(2, "")}
}
{set currentpercent to: 
    {currentfuel} per cento {Occasionally(2, "di capacità")}
}
{set title to Occasionally(3,"{F('Honorific')}")}

{if maxjump < 0.5:
   Pericolo!
   {shipsfuel} a zero!
   {Pause(500)}
   Passaggio al serbatoio di riserva. raccomando la disabilitazione di tutti i sistemi non vitali!
   {Pause(500)}
   Segnale di emergenza pronto all'attivazione, {title}
|elif maxjump < 1.25:
   {OneOf("attenzione:", "cautela:", "pericolo:", "attenzione:")}
   {shipsfuel} {OneOf("pericolosamente basso", "quasi a zero")}.
   {OneOf("prego rifornire", "rifornimento necessario")}, {title}
|elif currentfuel < 25:
   {OneOf("attenzione:", "cautela:", "pericolo:", "attenzione:")}
   {shipsfuel} {OneOf("inferiore al 25%", "{currentpercent}" )}, {title}
|elif currentfuel < 50:
   {shipsfuel} {OneOf("sotto il 50%", "{currentpercent}" )}, {title}
|elif currentfuel < 75:
   {Occasionally(2, "{shipsfuel} al {currentpercent}, {title}")}
|elif currentfuel < 100:
   {Occasionally(3, "{shipsfuel} al {currentpercent}, {title}")}
|else:
   {shipsfuel} al {OneOf(currentpercent, "massimo")}, {title}
}

