Glide.script:
{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'glide')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_body_name', event.body)}
{SetState('eddi_context_body_system', event.system)}

{if event.gliding:
    {Pause(5000)}
    Planata {Occasionally(2, "")} 
    {OneOf("in corso.", "iniziata.")}

    {Pause(1000)}
    {set body to BodyDetails(event.body, event.system)}
    {if !body.name || body.name = "" || !body.gravity:
        Dati di superficie non disponibili.
    |else:
        {if body.landable:
            {set gravity to round(body.gravity,2)}
            {if gravity > 4:
                Pericolo assoluto! {P(body.name)} è un corpo ad altissima gravità, stimabile in {gravity} G.
            |elif gravity > 2:
                Cautela! {P(body.name)} è un corpo ad alta gravità, stimabile in {gravity} G.
            |elif gravity > 0.5:
                Attenzione! {P(body.name)} è un corpo a media gravità, stimabile in {gravity} G.
            |else:
                {P(body.name)} è un corpo a bassa gravità, stimabile in {gravity} G.
            }
        }
    }
|else:
    Planata completata. Volo suborbitale in corso.
}

