{_ Missions route }
{_ Get the missions route for various mission details }


{if event.routetype = "expiring":
     {if event.distance > 0:
         {set mission to MissionDetails(event.missionids[0])}

         {if find(mission.typeEDName, "Passenger") > -1:
             {set type to "Transport "}
             {set type to cat(type, mission.passengertype)}
         |else:
             {set type to mission.type}
         }
         {set type to cat(type, " mission")}

         {type} {if mission.faction: for {mission.faction}}
         {if mission.destinationsystem: in the
             {if mission.destinationsystem = system.name:
                 current
             |else:
                 {mission.destinationsystem}
             } system
         } is expiring next.
     |else:
         No active missions found.
     }
|elif event.routetype = "farthest":
     {if event.distance > 0:
         Your {if missionsCount = 1: only |else: farthest}
         mission target is in the
         {if event.system = system.name:
             current system
         |else:
             {event.system} system, {round(event.distance, 1)} lightyears away
         }.
     |else:
         Mission not found with a designated target system.
     }
|elif event.routetype = "most":
     {if missionsCount = 1:
         Your only mission target is {Occasionally(2,"located")} in
         {if missions[0].destinationsystem = "":
             an unknown
         |elif missions[0].destinationsystem = system.name:
             the current
         |else:
             the {missions[0].destinationsystem}
         }
         {Occasionally(2,"star")} system.
     |else:
         {set systems to split(event.systems, "_")}
         {set system_most to event.system}
         {set systems_count to len(systems)}

         {if systems_count = 1:
             {if system_most = system.name:
                 {set system_most to "The current system"}
             |else:
                 {set system_most to cat("The ", system_most, " system")}
             }
             {OneOf("Most of your missions are {Occasionally(2,'located')} in {system_most}",
                 "{system_most} has {OneOf('the most','most of your')} missions",
                 "{system_most} has the most missions for you to complete")}.
         |elif systems_count > 1:
             You have {event.count} mission target{if event.count > 1:s} each, in the

             {set cur to 0}
             {while cur < len(equal):
                 {systems[cur]}
                 {set cur to cur + 1}
                 {if cur = systems_count-1: and |elif cur < systems_count: ,}
             } systems.
         }
     }
|elif event.routetype = "nearest":
     {if event.distance > 0:
         Your {if missionsCount = 1: only |else: nearest}
         mission target is in the
         {if event.system = system.name:
             current system
         |else:
             {event.system} system, {round(event.distance, 1)} lightyears away
         }.
     |else:
         Mission not found with a designated target system.
     }
|elif event.routetype = "route":
     {if event.routedistance > 0:
         Missions route calculated for {event.count} systems.
         Total route distance is {round(event.routedistance, 1)} lightyears.
         First mission destination is {event.system}, {round(event.distance, 1)} lightyears away.
     |else:
         Unable to calculate missions route.
     }
|elif event.routetype = "source":
     {if event.count > 0:
         {set systems to split(event.route, "_")}

         {set pos to 0}
         {while pos < event.count:

             {set source_cargo to []}
             {for missionid in event.missionids:
                 {set haulage to HaulageDetails(missionid)}
                 {if haulage.sourcesystem = systems[pos]:
                     {set cargo to CargoDetails(missionid)}
                     {if find(source_cargo, cargo.name) = -1:
                         {set source_cargo to cat(source_cargo, [cargo.name])}
                     }
                 }
             }

             {if pos = 0:
                 Your {if event.count = 1: only |else: nearest}
             |elif pos = 1:
                 Additionally, your
             |elif pos = event.count - 1:
                 Lastly, your
             |else:
                 Your
             }

             mission cargo source for
             {set cur to 0}
             {while cur < len(source_cargo):
                 {source_cargo[cur]}
                 {set cur to cur + 1}
                 {if cur = len(source_cargo) - 1: and |elif cur < len(source_cargo): ,}
             }

             is in the
             {if systems[pos] = system.name:
                 current system
             |else:
                 {systems[pos]} system,
                 {set distance to Distance(system.name, systems[pos])}
                 {round(distance, 1)} light years away
             }.
             {set pos to pos + 1}
         }
         {SetState('eddi_context_cargo_source', event.route)}
     |else:
         Cargo source not found with a designated target system.
     }
|elif event.routetype = "update":
     {if event.routedistance > 0:
         Next mission destination is {event.system}, {round(event.distance, 1)} lightyears away.
     |else:
         Unable to update to next mission destination.
     }
}

