Star report.script:
{_ Preferences _}
{set reportAge to 0}              {_ Set to 1 if you want to hear about the age of the star. _}
{set reportChromaticity to 0}     {_ Set to 1 if you want to hear about the colour of the star. _}
{set reportNotables to 1}         {_ Set to 0 if you don't want to hear about noteworthy main sequence star features. _}
{set reportScanValue to 0}        {_ Set to 1 if you want scan value reported _}
{set reportHabZone to 0}          {_ Set to 1 if you want habitable zone details for the primary star_}
{set mainSequenceFrequency to 2}  {_ Set to determine the frequency for describing stars as "main sequence" _}

{_ Fetch from context }
{if event: {set reportbody to event} }
{if !event: {set reportbody to BodyDetails(state.eddi_context_star_star, state.eddi_context_star_system)}}

{if !reportbody.name || reportbody.name = "":

    I'm not sure which star you are asking about.

|else:

    {set mainsequence to ["O", "B", "A", "F", "G", "K", "M"]}
    {for starclass in mainsequence:
        {if reportbody.stellarclass = starclass:
            {set mainsequence to 1}
        }
    }    

    {if mainsequence = 1 && reportNotables = 1:

        {set notables to []}
        {if reportbody.massprobability < 0.01:
            {set notables to cat(notables, [cat("an incredibly low solar mass of ", Humanise(reportbody.solarmass))])}
        |elif reportbody.massprobability < 0.1:
            {set notables to cat(notables, [cat("an extremely low solar mass of ", Humanise(reportbody.solarmass))])}
        |elif reportbody.massprobability < 1:
            {set notables to cat(notables, ["an unusually low solar mass"])}
        |elif reportbody.massprobability < 10:
            {set notables to cat(notables, ["lower than average solar mass"])}
        |elif reportbody.massprobability > 99.99:
            {set notables to cat(notables, [cat("an incredibly high solar mass of ", Humanise(reportbody.solarmass))])}
        |elif reportbody.massprobability > 99.9:
            {set notables to cat(notables, [cat("an extremely high solar mass of ", Humanise(reportbody.solarmass))])}
        |elif reportbody.massprobability > 99:
            {set notables to cat(notables, ["an unusually high solar mass"])}
        |elif reportbody.massprobability > 90:
            {set notables to cat(notables, ["higher than average solar mass"])}
        }

        {if reportbody.radiusprobability < 0.01:
            {set notables to cat(notables, [cat("an incredibly low solar radius of ", Humanise(reportbody.solarradius))])}
        |elif reportbody.radiusprobability < 0.1:
            {set notables to cat(notables, [cat("an extremely low solar radius of ", Humanise(reportbody.solarradius))])}
        |elif reportbody.radiusprobability < 1:
            {set notables to cat(notables, ["an unusually low solar radius"])}
        |elif reportbody.radiusprobability < 10:
            {set notables to cat(notables, ["lower than average solar radius"])}
        |elif reportbody.radiusprobability > 99.99:
            {set notables to cat(notables, [cat("an extremely high solar radius of ", Humanise(reportbody.solarradius))])}
        |elif reportbody.radiusprobability > 99.9:
            {set notables to cat(notables, [cat("an incredibly high solar radius of ", Humanise(reportbody.solarradius))])}
        |elif reportbody.radiusprobability > 99:
            {set notables to cat(notables, ["an unusually high solar radius"])}
        |elif reportbody.radiusprobability > 90:
            {set notables to cat(notables, ["higher than average solar radius"])}
        }

        {if reportbody.tempprobability < 0.01:
            {set notables to cat(notables, [cat("an incredibly low surface temperature of ", Humanise(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability < 0.1:
            {set notables to cat(notables, [cat("an extremely low surface temperature of ", Humanise(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability < 1:
            {set notables to cat(notables, ["an unusually low surface temperature"])}
        |elif reportbody.tempprobability < 10:
            {set notables to cat(notables, ["lower than average surface temperature"])}
        |elif reportbody.tempprobability > 99.99:
            {set notables to cat(notables, [cat("an extremely high surface temperature of ", Humanise(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability > 99.9:
            {set notables to cat(notables, [cat("an incredibly high surface temperature of ", Humanise(reportbody.temperature), " kelvin")])}
        |elif reportbody.tempprobability > 99:
            {set notables to cat(notables, ["an unusually high surface temperature"])}
        |elif reportbody.tempprobability > 90:
            {set notables to cat(notables, ["higher than average surface temperature"])}
        }
    }

    {_ Report notable main sequence stars and all stars not found on the main sequence _}
    {if len(notables) > 0 || mainsequence != 1:

        {if reportbody.stellarclass = "O":
            an incredibly rare {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif reportbody.stellarclass = "B":
            a very rare {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif reportbody.stellarclass = "A":
            a rare {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif reportbody.stellarclass = "F":
            an uncommon {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif reportbody.stellarclass = "G":
            a common {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif reportbody.stellarclass = "K":
            a common {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif reportbody.stellarclass = "M":
            a very common {P(reportbody.stellarclass)}-class 
            {Occasionally(mainSequenceFrequency, "main sequence")} star
        |elif find(["L", "T", "Y"], reportbody.stellarclass) > -1:
            {P(reportbody.stellarclass)}-class brown dwarf
        |elif reportbody.stellarclass = "TTS":
            {P(reportbody.stellarclass)}-class T tauri star 
            {Occasionally(2, "in the process of gravitational contraction")}
        |elif reportbody.stellarclass = "AEBE":
            Very young {P(reportbody.stellarclass)}-class proto-star 
            {Occasionally(2, "yet to enter main sequence 
                {Occasionally(2, \"but on the way to becoming an A or B class star\")}"
            )}
        |elif reportbody.stellarclass = "WC":
            {Occasionally(2, "fast burning ")} {P(reportbody.stellarclass)}-class carbon rich Wolf-Rayet star 
            {Occasionally(2, ", shedding considerable mass and exposing ionised carbon at its core, ")}
        |elif reportbody.stellarclass = "WN":
            {Occasionally(2, "fast burning ")} {P(reportbody.stellarclass)}-class helium and nitrogen rich Wolf-Rayet star 
            {Occasionally(2, ", shedding considerable mass and exposing ionised helium and nitrogen at its core, ")}
        |elif reportbody.stellarclass = "WO":
            {Occasionally(2, "fast burning ")} {P(reportbody.stellarclass)}-class oxygen rich Wolf-Rayet star 
            {Occasionally(2, ", shedding considerable mass and exposing ionised oxygen at its core, ")}
        |elif reportbody.stellarclass = "WNC":
            {Occasionally(2, "fast burning ")} {P(reportbody.stellarclass)}-class Wolf-Rayet star 
            {Occasionally(2, ", shedding considerable mass and exposing ionised nitrogen, carbon, oxygen, and helium at its core, ")}
        |elif reportbody.stellarclass = "W":
            {Occasionally(2, "fast burning ")} {P(reportbody.stellarclass)}-class Wolf-Rayet star 
            {Occasionally(2, ", shedding considerable mass and exposing heavier elements at its core, ")}
        |elif reportbody.stellarclass = "CS":
            {P(reportbody.stellarclass)}-class carbon star
        |elif find(["C", "CN", "CH", "CHd"], reportbody.stellarclass) > -1:
            {Occasionally(2, "late-sequence")} {P(reportbody.stellarclass)}-class carbon-rich star
        |elif reportbody.stellarclass = "CJ":
            {Occasionally(2, "late-sequence")} {P(reportbody.stellarclass)}-class carbon star {Occasionally(2, "with high levels of carbon 13 in its atmosphere")}
        |elif reportbody.stellarclass = "MS":
            Late sequence {P(reportbody.stellarclass)}-class star {Occasionally(4, "that was once an M class star and is on the way to becoming a carbon star")}
        |elif reportbody.stellarclass = "S":
            Late sequence {P(reportbody.stellarclass)}-class star {Occasionally(4, "that was once an M class star and is becoming a carbon star")}
        |elif reportbody.stellarclass = "D":
            {P(reportbody.stellarclass)}-class white dwarf
        |elif reportbody.stellarclass = "DA":
            {P(reportbody.stellarclass)}-class white dwarf {Occasionally(4, "with a hydrogen-rich atmosphere")}
        |elif reportbody.stellarclass = "DAB":
            {P(reportbody.stellarclass)}-class white dwarf {Occasionally(4, "with an atmosphere rich in hydrogen and helium")}
        |elif slice(reportbody.stellarclass, 0, 1) = "D":
            {P(reportbody.stellarclass)}-class white dwarf
        |elif slice(reportbody.stellarclass, 0, 1) = "N":
            {P(reportbody.stellarclass)}-class neutron star {Occasionally(4, "that has a dangerously high-density core")}
        |elif reportbody.stellarclass = "H":
            {P(reportbody.stellarclass)}-class black hole
        |elif reportbody.stellarclass = "X":
            Exotic stellar entity
        |elif reportbody.stellarclass = "SuperMassiveBlackHole":
            Supermassive black hole
        |elif reportbody.stellarclass = "A_BlueWhiteSuperGiant":
            {P(reportbody.stellarclass)}-class blue-white super-giant {Occasionally(4, "that is expanding towards being a red super-giant")}
        |elif reportbody.stellarclass = "F_WhiteSuperGiant":
            {P(reportbody.stellarclass)}-class yellow-white supergiant {Occasionally(4, "that is expanding towards being a red super-giant")}
        |elif reportbody.stellarclass = "M_RedSuperGiant":
            {P(reportbody.stellarclass)}-class red super-giant {Occasionally(4, "in its helium burning phase")}
        |elif reportbody.stellarclass = "M_RedGiant":
            {P(reportbody.stellarclass)}-class red giant {Occasionally(4, "that is expanding towards being a red super-giant")}
        |elif reportbody.stellarclass = "K_OrangeGiant":
            {P(reportbody.stellarclass)}-class orange giant {Occasionally(4, "that is expanding as it heads towards the end of its life")}
        |elif reportbody.stellarclass = "RoguePlanet":
            Rogue planet
        |elif reportbody.stellarclass = "Nebula":
            Nebula
        |elif reportbody.stellarclass = "StellarRemnantNebula":
            Nebula remnant
        |else:
            Class {Spacialise(reportbody.stellarclass)} star
        }

        {if reportAge = 1:
            {set ageInYears to reportbody.age * 1000000}
            {if reportbody.age > 0:
                {OneOf("that has been around for {Humanise(ageInYears)} years",
                       "that is {Humanise(ageInYears)} years old",
                       "of {Humanise(ageInYears)} years",
                )}
            |else:
                that has been in existence for less than one million years,
            }
            {if ageInYears < 1000000:
                making it one of the youngest stars in the galaxy
            |elif ageInYears >14000000000:
                making it one of the oldest stars in the galaxy
            }
        }

        {OneOf("detected", "identified")}.

        {if mainsequence = 1 && reportChromaticity = 1:
            {Occasionally(2,"As you can see, it emits a {reportbody.chromaticity} light.")}
        }

        {if reportScanValue: {F("Report last scan value")} }

        {if event.mainstar && reportHabZone : {F("Star habitable zone")} } 

    }   
}

